import React from "react";
import axios from "axios";

export default class Subscription extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            collections: []
        }
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: "https://www.googleapis.com/youtube/v3/subscriptions?part=snippet%2CcontentDetails&mine=true&maxResults=20&key=AIzaSyCYnz2BIh3II4v9LlDizrr6F2aj7LVCAU0",
            headers: {
                "Authorization": "Bearer ya29.GlxxB3xVn8vzrqT7UWa9TxhJmf2g5un-s5dSmhE_Nv5rjowyFe_Hds8MFnCVuY8xaTsESWZY-iNZHkVjzHBPhx1TUx-7brXEAqCg8dPTzNHJjyI-v7luZnRV884Y8w"
            }
        })

            .then((response) => {
                console.log(response.data)
                console.log(response.data.items[0].snippet.title)
                this.setState({
                    collections: [...response.data.items],
                });
            })
    }
    render() {
        return (
            <div>
                <h1>Subscription channels</h1>
                <h1 className="display-4 text-center text-success">Your Subscribed Channels</h1>
                <div className="container-fluid">
                    <div className="row">
                        {this.state.collections.map((item) => {
                            return (
                                <div class="card shadow-lg col-lg-4 col-sm-5 mt-2">
                                    {/* <iframe height="350px" src={"https://www.youtube.com/embed/" + item.id.videoId} frameBorder="0"
                                        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe> */}
                                    <div class="card-body">
                                        <h4 style={{ fontFamily: "Ropa Sans, sans-serif" }} class="card-title">{item.snippet.title}</h4>
                                        {/* <h2 style={{ fontFamily: "Ropa Sans, sans-serif" }} class="card-title">Total Videos: {item.contentDetails}</h2> */}
                                    </div>
                                </div>
                            )
                        })
                        }
                    </div>
                </div>
            </div>
        );
    }
}
