import React from 'react';
import GoogleLogin from 'react-google-login';
import { GoogleLogout } from 'react-google-login';

const responseGoogle = (response) => {
    console.log(response);
}


class Login extends React.Component {

    render() {
        return (
            <div>
                <div className="container shadow-lg" style={{ width: "500px", height: "600px" }}>
                    <img src="login.png"
                        style={{ width: "100px;", height: "100px;" }} className="text-cenetr"></img><br /><br />
                    <GoogleLogin className="text-cenetr"
                        clientId="582857054355-drok1a2ag8r1e8c6sr60a4kael5cf6sg.apps.googleusercontent.com"
                        buttonText="Login"
                        onSuccess={responseGoogle}
                        onFailure={responseGoogle}
                        cookiePolicy={'single_host_origin'} >
                        <FontAwesome
                            name='google'
                        />
                        <span> Login with Google</span>
                    </GoogleLogin>
                    {/* <GoogleLogout
                        clientId="582857054355-drok1a2ag8r1e8c6sr60a4kael5cf6sg.apps.googleusercontent.com"
                        buttonText="Logout"
                        onLogoutSuccess={logout}
                    >
                    </GoogleLogout> */}
                    <div className="mb-3"></div>
                </div>
            </div>
        );
    }
}
export default Login;
